import React, {useState} from 'react'
import {View, Button, TextInput, ScrollView, StyleSheet} from 'react-native'

const CreateUserScreen = () => {
    const [state, setState] = useState({
        name:"",
        email:"",
        phone:"",
    });

    const handleChange = (name, value)=>{
        setState({...state, [name]: value})
    }

    return (
        <ScrollView style={styles.container}>
            <View style={styles.inputGroup}>
                <TextInput placeholder="Name User" onChange={(value)=> {handleChange("name", value)}}></TextInput>
            </View>
            <View style={styles.inputGroup}>
                <TextInput placeholder="Email User" onChange={(value)=> handleChange("email", value)}></TextInput>
            </View>
            <View style={styles.inputGroup}>
                <TextInput placeholder="Phone User" onChange={(value)=> handleChange("phone", value)}></TextInput>
            </View>
            <View>
                <Button title="Send" onPress={()=> console.log(state)}></Button>
            </View>
        </ScrollView>
    )
}

const styles = StyleSheet.create({
    container:{
        flex: 1,
        padding: 35,
    },
    inputGroup:{
        flex: 1,
        padding: 10,
        marginBottom: 25,
        borderBottomWidth: 1,
        borderBottomColor: '#cccccc',
    },
})


export default CreateUserScreen
